/* ==UserStyle==
@name           Readeck
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

* {
    scrollbar-width: thin;
  }

  .bookmark-content {
    color: #101010
  }

  .layout-topnav {
    opacity: 0.4;
    filter: grayscale(100%);

    &:hover {
      opacity: unset;
      filter: unset;
    }
  }

  .bookmark-topbar {
    filter: grayscale(100%);
  }

  .bookmark-sidebar {
    opacity: 0.7;
    filter: grayscale(100%);

    &:hover {
      opacity: unset;
      filter: unset;
    }
  }

  @media only screen and (max-width: 900px) {
  body.js .sidemenu--hidden {
    visibility: hidden;
    --tw-translate-x: -105%;
  }

  body.js .sidemenu {
    bottom: 0;
    left: 0;
    position: fixed;
    top: 0;
    transition-delay: .1s;
    transition-duration: .15s;
    transition-property: transform;
    transition-timing-function: cubic-bezier(.4,0,.2,1);
    visibility: visible;
    z-index: 50;
    --tw-translate-x: 0px;
  }

  .layout {
    display: flex;
    flex-direction: column;
  }
}

